import {Component} from '@angular/core';
import {interval} from 'rxjs';

@Component({
    selector: 'app-unsubscribe',
    templateUrl: './unsubscribe.component.html',
    styleUrls: ['./unsubscribe.component.css']
})
export class UnsubscribeComponent {
    counter = interval(1000);
    data1: number[] = [];
    data2: number[] = [];
    data3: number[] = [];
    subscriber1;


    OnSubscribe1() {
        this.subscriber1 = this.counter.subscribe((value) => {
            this.data1.push(value);
        });
    }

    OnSubscribe2() {
        this.subscriber1 = this.counter.subscribe((value) => {
            this.data2.push(value);
        });
    }

    OnSubscribe3() {
        this.subscriber1 = this.counter.subscribe((value) => {
            this.data3.push(value);
        });
    }

    OnUnSubscribe() {
        this.subscriber1.unsubscribe();
    }
}
<h1>Unsubscribing to an Observable</h1>
<div class="div-container">
    <div class="sub-container">
        <h2>Subscriber 1</h2>
        <hr/>
        <div class="data-list" *ngFor="let x of data1">
            {{ x }}
        </div>
        <button (click)="OnSubscribe1()">Subscribe</button>
        <button (click)="OnUnSubscribe1()">Unsubscribe</button>
    </div>

    <div class="sub-container">
        <h2>Subscriber 1</h2>
        <hr/>
        <div class="data-list" *ngFor="let x of data2">
            {{ x }}
        </div>
        <button (click)="OnSubscribe2()">Subscribe</button>
        <button (click)="OnUnSubscribe2()">Unsubscribe</button>
    </div>

    <div class="sub-container">
        <h2>Subscriber 1</h2>
        <hr/>
        <div class="data-list" *ngFor="let x of data3">
            {{ x }}
        </div>
        <button (click)="OnSubscribe3()">Subscribe</button>
        <button (click)="OnUnSubscribe3()">Unsubscribe</button>
    </div>
</div>
